/*#################################################################################################################################################################################################
	GLOBAL VARIABLES
#################################################################################################################################################################################################*/
	// Message Variables
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
		var topic			= msg.topic;
		var payload			= msg.payload;	//
		var unitID          = "gw6A68C63ADAD23A";
		msg.payload         = null;
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

	// Topic Variables
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
		var systemType		= "";			// GW
		var systemID		= "";			// gw6A68C63ADAF88F
		var subID_1			= "";			// 1
		var subID_2			= "";			// A
		var subID_3			= "";			//
		var subID_4			= "";			//
		var subID_5			= "";			//
		var subID_6			= "";			//
		var subID_7			= "";			//
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

	// Index Variables
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
		var topicLength		= topic.length;
		var topicSeperator	= topic.indexOf("/");
		var indexGW_ID		= -1;
		var indexGW_Unit	= -1;
		var indexGW_Func	= -1;
		var index_5			= -1;
		var index_6			= -1;
		var index_7			= -1;
		var index_8			= -1;
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

	// Memory Variables
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
		var	_unitID;
		var	_geyserDataSN;
		var	_geyserData;
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

	// Time & Date variables
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
		var date			= new Date();

		var system_Year		= String( date.getFullYear() );
		var system_Month	= String( date.getMonth() + 1 );
		var system_Day		= String( date.getDate() );
		var system_Hour		= String( date.getHours() );
		var system_Minute	= String( date.getMinutes() );
		var system_Second	= String( date.getSeconds() );

		system_Year			= "00".substr(system_Year.length)		+ system_Year;
		system_Month		= "00".substr(system_Month.length)		+ system_Month;
		system_Day			= "00".substr(system_Day.length)		+ system_Day;
		system_Hour			= "00".substr(system_Hour.length)		+ system_Hour;
		system_Minute		= "00".substr(system_Minute.length)		+ system_Minute;
		system_Second		= "00".substr(system_Second.length)		+ system_Second;

		var system_time		= system_Year + "/" + system_Month + "/" + system_Day + " " + system_Hour + ":" + system_Minute;
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

	// Output Variables
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
		// Output array
		//-----------------------------------------------------------------------------------------
			var output		= [];
		//-----------------------------------------------------------------------------------------

		// Output array components
		//-----------------------------------------------------------------------------------------
			var output_sys_t;
			var output_unitUpdate_CS	= null;
			var output_debug	        = null;
		//-----------------------------------------------------------------------------------------
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~


/*#################################################################################################################################################################################################
	FUNCTIONS
#################################################################################################################################################################################################*/
	// Topic Breakdown
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
		systemType	= topic;

		if (topicSeperator >= 0)
		{
			systemType	= topic.slice(0, topicSeperator);
			systemID	= topic.slice(topicSeperator + 1);

			indexGW_ID		= topic.indexOf("/", topicSeperator + 1);
			if (indexGW_ID >= 0)
			{
				systemID	= topic.slice(topicSeperator + 1, indexGW_ID);
				subID_1		= topic.slice(indexGW_ID + 1);

				indexGW_Unit	= topic.indexOf("/", indexGW_ID + 1);
				if (indexGW_Unit >= 0)
				{
					subID_1		= topic.slice(indexGW_ID + 1, indexGW_Unit);
					subID_2		= topic.slice(indexGW_Unit + 1);

					indexGW_Func	= topic.indexOf("/", indexGW_Unit + 1);
					if (indexGW_Func >= 0)
					{
						subID_2		= topic.slice(indexGW_Unit + 1, indexGW_Func);
						subID_3		= topic.slice(indexGW_Func + 1);

						index_5		= topic.indexOf("/", indexGW_Func + 1);
						if (index_5 >= 0)
						{
							subID_3		= topic.slice(indexGW_Func + 1, index_5);
							subID_4		= topic.slice(index_5 + 1);

							index_6		= topic.indexOf("/", index_5 + 1);
							if (index_6 >= 0)
							{
								subID_4		= topic.slice(index_5 + 1, index_6);
								subID_5		= topic.slice(index_6 + 1);

								index_7		= topic.indexOf("/", index_6 + 1);
								if (index_7 >= 0)
								{
									subID_5		= topic.slice(index_6 + 1, index_7);
									subID_6		= topic.slice(index_7 + 1);

									index_8		= topic.indexOf("/", index_7 + 1);
									if (index_8 >= 0)
									{
										subID_6		= topic.slice(index_7 + 1, index_8);
										subID_7		= topic.slice(index_8 + 1);
									}
								}
							}
						}
					}
				}
			}
		}
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

	// Contents Generation
	//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
		if( topicSeperator > -1 )
		{
			if( systemType === "GW" )
			{
				if( (indexGW_ID > -1)&&( indexGW_Unit > -1 )&&(systemID === unitID) )
				{
				    output_debug    = msg;
					_unitID			= systemID;
					_geyserDataSN	= "_geyserTestData_" + _unitID;

					_geyserData		= global.get(_geyserDataSN)||{
										"SN":	_unitID,
										"CS":{
												"S":	0,
												"I":	0,
												"t":{
														"Y":	system_Year,
										    			"M":	system_Month,
														"D":	system_Day,
											    		"h":	system_Hour,
														"m":	system_Minute,
														"s":	system_Second
													}
											},
											"A":	false,
											"AVG":	false,
