														"PV":		0.0
													},
													"Pt":	{
												            "Raw":  {
    																"I2AC":	0.0,
																	"I2PV":	0.0
											                    },
												            "kWh":  {
																	"AC":	0.0,
																	"PV":	0.0
													            }
													        },
													"STS":{
														"Tr_Th":	0,
														"Tr_Te":	0,
														"Tr_DR":	0,
														"Tr_CS":	0,
														"Tr_PCB":	0,
														"A_Sc":	    0,
														"A_An":	    0,
