    																"I2AC":	0.0,
																	"I2PV":	0.0
											                    },
												            "kWh":  {
																	"AC":	0.0,
																	"PV":	0.0
													            }
													        },
													"STS":{
														"Tr_Th":	0,
														"Tr_Te":	0,
														"Tr_DR":	0,
														"Tr_CS":	0,
														"Tr_PCB":	0,
														"A_Sc":	    0,
														"A_An":	    0,
														"A_DT":	    0,
														"O_AC":	    0,
														"O_PV":	    0,
														"T":        0,
														"H":        0
													},
													"SET":{
														"O":{
														    "ACM": 0,
														    "ACO": 0,
														    "PVM": 0
														},
														"S":{
														    "ACT": 0.0,
														    "ACOT": 0.0,
														    "PVT": 0.0,
														    "ACCT": 0.0
														},
														"A":{
														    "DRT": 0.0,
														    "SCB": 0.0
														},
													}
												}
										    }

					if( (subID_1 === "S")&&( subID_2 === "CW" ) )
					{
						if( ( _geyserData.CS.S !== payload.S )||( _geyserData.CS.I !== payload.I ) )
						{
							_geyserData.CS.S	=	payload.S;
							_geyserData.CS.I	=	payload.I;
							_geyserData.CS.t	=	{
											    		"Y":	system_Year,
														"M":	system_Month,
														"D":	system_Day,
														"h":	system_Hour,
														"m":	system_Minute,
														"s":	system_Second
													}
						}
					}
                    else if( subID_1 === "1" )
					{
						if( subID_2 === "A" )
						{
							if( "T" in payload )
							{
								if( "G" in payload.T  )
								{
									_geyserData.G1.T.G	= payload.T.G;
