									_geyserData.AVG		= true;
								}

								if( "PCB" in payload.T  )
								{
									_geyserData.G1.T.PCB		= payload.T.PCB;
									_geyserData.A		= true;
									_geyserData.AVG		= true;
								}
							}
							if( "I" in payload )
							{
								if( "AC" in payload.I  )
								{
									_geyserData.G1.I.AC		= payload.I.AC;
									_geyserData.A		= true;
									_geyserData.AVG		= true;
								}

								if( "PV" in payload.I  )
								{
									_geyserData.G1.I.PV		= payload.I.PV;
									_geyserData.A		= true;
									_geyserData.AVG		= true;
								}
							}
						}
						else if( subID_2 === "a" )
						{
							if( "I" in payload )
							{
								if( "AC" in payload.I  )
								{
									_geyserData.G1.Pt.Raw.I2AC	= payload.I.AC;
									_geyserData.G1.Pt.kWh.AC	= (payload.I.AC)*(230*230/3000)/1000;
									_geyserData.A		= true;
									_geyserData.AVG		= true;
								}

								if( "PV" in payload.I  )
								{
									_geyserData.G1.Pt.Raw.I2PV	= payload.I.PV;
									_geyserData.G1.Pt.kWh.PV	= (payload.I.PV)*(230*230/3000)/1000;
									_geyserData.A		= true;
									_geyserData.AVG		= true;
								}
							}
						}
						else if( subID_2 === "STS")
						{
						    if( "A" in payload)
						    {
						        _geyserData.G1.STS.Tr_Th        = payload.A.Tr_Th;
						        _geyserData.G1.STS.Tr_Te        = payload.A.Tr_Te;
						        _geyserData.G1.STS.Tr_DR        = payload.A.Tr_DR;
						        _geyserData.G1.STS.Tr_CS        = payload.A.Tr_CS;
