						        _geyserData.G1.STS.A_Sc         = payload.A.A_Sc;
						        _geyserData.G1.STS.A_An         = payload.A.A_An;
						        _geyserData.G1.STS.A_DT         = payload.A.A_DT;
						    }
						    if( "O" in payload)
						    {
						        if("AC" in payload.O)
						        {
						            _geyserData.G1.STS.O_AC     = payload.O.AC;
						        }
						        if("PV" in payload.O)
						        {
						            _geyserData.G1.STS.O_PV     = payload.O.PV;
						        }
						    }
						    if("T" in payload)
						    {
						        _geyserData.G1.STS.T            = payload.T;
						    }
						    if("H" in payload)
						    {
						        _geyserData.G1.STS.H            = payload.H;
						    }
						}
						else if (subID_2 === "SET")
						{
						    if("O" in payload)
						    {
						        if("ACM" in payload.O)
						        {
						            _geyserData.G1.SET.O.ACM    = payload.O.ACM;
						        }
						        if("ACO" in payload.O)
						        {
						            _geyserData.G1.SET.O.ACO    = payload.O.ACO;
						        }
						        if("PVM" in payload.O)
						        {
						            _geyserData.G1.SET.O.PVM    = payload.O.PVM;
						        }
						    }
						    if("S" in payload)
						    {
						        if("ACT" in payload.S)
						        {
						            _geyserData.G1.SET.S.ACT        = payload.S.ACT;
						        }
						        if("ACOT" in payload.S)
						        {
						            _geyserData.G1.SET.S.ACOT        = payload.S.ACOT;
						        }
						        if("PVT" in payload.S)
						        {
						            _geyserData.G1.SET.S.PVT        = payload.S.PVT;
						        }
						        if("ACCT" in payload.S)
						        {
						            _geyserData.G1.SET.S.ACCT       = payload.S.ACCT;
						        }
						    }
						    if("A" in payload)
						    {
						        if("DRT" in payload.A)
						        {
						            _geyserData.G1.SET.A.DRT        = payload.A.DRT;
						        }
						        if("SCB" in payload.A)
						        {
						            _geyserData.G1.SET.A.SCB        = payload.A.SCB;
						        }
						    }
						}
					}   // end if unit 1
                    else if( subID_1 === "2" )
					{
						if( subID_2 === "A" )
						{
							if( "T" in payload )
							{
								if( "G" in payload.T  )
								{
									_geyserData.G2.T.G			= payload.T.G;
									_geyserData.A		= true;
									_geyserData.AVG		= true;
								}

								if( "PCB" in payload.T  )
								{
									_geyserData.G2.T.PCB		= payload.T.PCB;
									_geyserData.A		= true;
									_geyserData.AVG		= true;
								}
							}
							if( "I" in payload )
							{
								if( "AC" in payload.I  )
								{
									_geyserData.G2.I.AC		= payload.I.AC;
									_geyserData.A		= true;
