								}

								if( "PV" in payload.I  )
								{
									_geyserData.G2.I.PV		= payload.I.PV;
									_geyserData.A		= true;
									_geyserData.AVG		= true;
								}
							}
						}
						else if( subID_2 === "a" )
						{
							if( "I" in payload )
							{
								if( "AC" in payload.I  )
								{
									_geyserData.G2.Pt.Raw.I2AC	= payload.I.AC;
									_geyserData.G2.Pt.kWh.AC	= (payload.I.AC)*(230*230/3000)/1000;
								}

								if( "PV" in payload.I  )
								{
									_geyserData.G2.Pt.Raw.I2PV	= payload.I.PV;
									_geyserData.G2.Pt.kWh.PV	= (payload.I.PV)*(230*230/3000)/1000;
								}
							}
						}
						else if( subID_2 === "STS")
						{
						    if( "A" in payload)
						    {
						        _geyserData.G2.STS.Tr_Th        = payload.A.Tr_Th;
						        _geyserData.G2.STS.Tr_Te        = payload.A.Tr_Te;
						        _geyserData.G2.STS.Tr_DR        = payload.A.Tr_DR;
						        _geyserData.G2.STS.Tr_CS        = payload.A.Tr_CS;
						        _geyserData.G2.STS.Tr_PCB       = payload.A.Tr_PCB;
						        _geyserData.G2.STS.A_Sc         = payload.A.A_Sc;
						        _geyserData.G2.STS.A_An         = payload.A.A_An;
						        _geyserData.G2.STS.A_DT         = payload.A.A_DT;
						    }
						    if( "O" in payload)
						    {
						        if("AC" in payload.O)
						        {
						            _geyserData.G2.STS.O_AC     = payload.O.AC;
						        }
						        if("PV" in payload.O)
						        {
						            _geyserData.G2.STS.O_PV     = payload.O.PV;
						        }
						    }
						    if("T" in payload)
						    {
						        _geyserData.G2.STS.T            = payload.T;
						    }
						    if("H" in payload)
						    {
						        _geyserData.G2.STS.H            = payload.H;
						    }
						}
						else if (subID_2 === "SET")
						{
						    if("O" in payload)
						    {
						        if("ACM" in payload.O)
						        {
						            _geyserData.G2.SET.O.ACM    = payload.O.ACM;
						        }
