					}   // end if unit 2

                    // Reset Variables to 0 if Comms is lost
    			    if(_geyserData.CS.S === 0)
    			    {
    					_geyserData.G1.T.G		= 0;
    					_geyserData.G1.T.PCB	= 0;
    					_geyserData.G1.I.AC	    = 0;
    					_geyserData.G1.I.PV	    = 0;
