msg.payload = _geyserData; // _geyserData
return msg;

var unit = flow.get("unit");
var desc = "Centurion"
var PVT, ACT, ACOT;
var OverTemp, UnderTemp, DryBurn, Contactor, PCBTemp, Scale, Anode, DripTray, ACOn, PVOn;
var ACM, ACO, PVM;
var Temperature, kWhAC, kWhPV, Eff, ACCost, PVCost, carbon;
var alarms;
var R_kWh = parseFloat(global.get("R_kW"));

var status = flow.get("status");
    OverTemp = status.OverTemp || 0;
    UnderTemp = status.UnderTemp || 0;
    DryBurn = status.DryBurn || 0;
    Contactor = status.Contactor || 0;
    PCBTemp = status.PCBTemp || 0;
    Scale = status.Scale || 0;
    Anode = status.Anode || 0;
    DripTray = status.DripTray || 0;
    ACOn = status.ACOn || 0;
    PVOn = status.PVOn || 0;

ACM     =   flow.get("ACOn") || 0;
PVM     =   flow.get("PVOn") || 0;
ACO     =   flow.get("ACOverride") || 0;
ACT     =   flow.get("ac_setpoint") || 0; 
ACOT    =   flow.get("override_sp") || 0; 
PVT     =   flow.get("pv_setpoint") || 0; 

Temperature = parseFloat(flow.get("temperature"));
kWhAC = parseFloat(flow.get("kWhAC"));
kWhPV = parseFloat(flow.get("kWhPV"));
Eff = parseFloat((kWhPV / (kWhAC+kWhPV)*100).toFixed(2));
var cost = kWhAC * 2.07;
ACCost = parseFloat(cost.toFixed(2));
cost = kWhPV * 2.07;
PVCost = parseFloat(cost.toFixed(2));
var carbon1 = (kWhPV * 0.7)/1000;
carbon = parseFloat(carbon1.toFixed(2));

if(OverTemp==1 || UnderTemp==1 || DryBurn==1 || Contactor==1 || PCBTemp==1 || Scale==1 || Anode==1 || DripTray==1)
{
    if(OverTemp==1){alarms="Over Temperature"}
    if(UnderTemp==1){alarms="Low Temperature"}
    if(DryBurn==1){alarms="Dry Burn Alarm"}
    if(Contactor==1){alarms="Contactor Fault"}
    if(PCBTemp==1){alarms="PCB Temperature Hi"}
    if(Scale==1){alarms="Scale Build-up"}
    if(Anode==1){alarms="Anode Depletion Alarm"}
    if(DripTray==1){alarms="Driptray Water Alarm"}
}
else
{
    alarms = "None";
}
var gwState = flow.get("state");
var _data = {};
_data = {
    "unit": unit,
    "desc": desc,
