    if((msg.payload == isNaN) || (msg.payload === null) || (msg.payload === ""))
    {
        hour        = data.ontime.substring(0, 2);
        minute      = data.ontime.substring(3, 5);
        data.ontime = hour+':'+minute;
    }else{
        now     = new Date(msg.payload);
        hour        = now.getHours()-2;
        minute      = now.getMinutes();

        if(hour.toString().length == 1) {hour = '0'+hour;}
        if(minute.toString().length == 1) {minute = '0'+minute;}

        data.ontime = hour+':'+minute;
    }
}
if(msg.topic == "T1OffTime"){
    if((msg.payload == isNaN) || (msg.payload === null) || (msg.payload === ""))
    {
        hour        = data.ontime.substring(0, 2);
        minute      = data.ontime.substring(3, 5);

        data.ontime = hour+':'+minute;
    }else{
    var Offnow  = new Date(msg.payload);
